<div class="container" id="bakery-items">
    <!-- Bakery items dynamically rendered here -->
</div>

<script>
    // Sample bakery items
    const bakeryItems = [
        { id: 1, name: "Chocolate Cake", price: "$10", img: "data:image/jpeg;base64," },
        { id: 2, name: "Croissant", price: "$2", img: "data:image/jpeg;base64," },
        { id: 3, name: "Apple Pie", price: "$8", img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQQYcu3RzXSn24QVayqcBTA4JAzi9EztUwH-A&s" },
        { id: 4, name: "Muffin", price: "$3", img: "data:image/jpeg;base64," },
        { id: 5, name: "Bread Loaf", price: "$5", img: "data:image/jpeg;base64," },
        { id: 6, name: "Donuts", price: "$8", img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRj4Lk2zhaHaNowDxe_KI6BnBih0HyikzNBwg&s" },
        { id: 7, name: "Cupcake", price: "$3", img: "data:image/jpeg;base64," },
        { id: 8, name: "Pastry", price: "$9", img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRBHC4b-blG8yN3iavVofKtSLRu26N5owNCNA&s" },
        { id: 9, name: "Chocolate brownie", price: "$15", img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSRH5AquUCI9jfBcKfVo-4FH5vtRJ9SXuFEhQ&s" },
        { id: 10, name: "Cheesecake", price: "$25", img: "data:image/jpeg;base64," }
    ];

    // Function to render bakery items
    function renderBakeryItems() {
        const bakeryContainer = document.getElementById("bakery-items");
        bakeryItems.forEach(item => {
            const card = document.createElement("div");
            card.className = "card";
            card.innerHTML = `
                <img src="${item.img}" alt="${item.name}">
                <h3>${item.name}</h3>
                <p>Price: ${item.price}</p>
                <button onclick="addToCart(${item.id})">Add to Cart</button>
            `;
            bakeryContainer.appendChild(card);
        });
    }

    // Function to add item to cart (stored in localStorage)
    function addToCart(id) {
        let cart = JSON.parse(localStorage.getItem("cart")) || [];
        const selectedItem = bakeryItems.find(item => item.id === id);
        cart.push(selectedItem);
        localStorage.setItem("cart", JSON.stringify(cart));
        alert(`${selectedItem.name} added to cart.`);
    }

    renderBakeryItems();
</script>

